mapper.sql:
FROM ( SELECT v.aid, v.title, z.keyword, row_number ( ) over ( PARTITION BY v.aid, c.content_id ORDER BY v.aid, z.keyword_index ) row_num, c.content_id FROM mug_video v JOIN ( SELECT * FROM mug_keyword k WHERE k.game_id = 'DSSQ' ORDER BY k.keyword_index ) z ON instr( REPLACE ( v.title, ' ', '' ), z.keyword ) > 0 JOIN mug_video_content c ON c.aid = v.aid AND c.type = 'G' WHERE v.aid NOT IN ( SELECT d.aid FROM mug_dssq d ) and date( v.pub_time ) >= str_to_date(#{startDate},'%Y-%m-%d')
and str_to_date(#{endDate}, '%Y-%m-%d') >= date( v.pub_time )
) r WHERE r.row_num = 1 AND ( r.keyword != '疯狂' OR r.content_id != 20 ) AND ( r.keyword != '爆炸' OR r.content_id != 26 )
