mapper.sql:
select r.aid aid, r.game_id game_id, r.keyword keyword, current_timestamp() update_time from ( select v.aid, v.title, z.*, row_number ( ) over ( PARTITION by v.aid order by v.aid, z.filter_index, z.keyword_index ) row_num from mug_video v join ( select g.game_id, g.common_name, g.filter_index, k.keyword, k.keyword_index from mug_game g join mug_keyword k on g.game_id = k.game_id order by g.filter_index, k.keyword_index ) z on instr( REPLACE ( v.title, ' ', '' ), z.keyword ) > 0 where v.aid not in ( select c.aid from mug_game_content c ) and date( v.pub_time ) >= str_to_date(#{startDate},'%Y-%m-%d')
and str_to_date(#{endDate}, '%Y-%m-%d') >= date( v.pub_time )
) r where r.row_num = 1
